import React from "react";
import "./App.css";
import Question from "./components/Question";
import Answer from "./components/Answer";

type QuestionType = {
  category: string;
  type: string;
  difficulty: string;
  question: string;
  correct_answer: string;
  incorrect_answers: string[];
};

type S = {
  loading: boolean;
  questions: QuestionType[];
};

class App extends React.Component<{}, S> {
  private title: string = "Welcome to the Quiz";

  state: S = {
    loading: true,
    questions: [],
  };

  constructor(props: {}) {
    super(props);
  }

  componentDidMount() {
    this.getQuestions();
  }

  getQuestions=()=> {
    fetch(
      "https://opentdb.com/api.php?amount=10&category=27&difficulty=easy&type=multiple"
    )
      .then((res) => res.json())
      .then((json) => {
        this.setState({ loading: false, questions: json.results });
      });
  }

  render() {
    return (
      <div className="App">
        <div id="title">{this.title}</div>

        <button id="start" onClick={this.getQuestions}>
          Start
        </button>

        {this.state.loading ? (
          <div>Loading questions... </div>
        ) : (
          <>
            <Question content={this.state.questions[0].question} />
            <Answer
              content={[
                ...this.state.questions[0].incorrect_answers,
                this.state.questions[0].correct_answer,
              ]}
            />
          </>
        )}

        {/* <Submit></Submit> */}
      </div>
    );
  }
}

export default App;
