import React from 'react';
import './App.css';

class App extends React.Component {
	private title: string = 'Welcome to the Quiz!'
	private data = {
		question:'What is the fastest  land animal?',
		correct_answer:'Cheetah',
		incorrect_answer:[
			'Lion',
			'Thomson&rsquo;s Gazelle',
			'Pronghorn Antelope'
			]
	}

	render(){
		return (
			<div className="App">
				<div id="title">
					{this.title}
				</div>
				<Question content={this.data.question}></Question>
				<Answers content={[...this.data.incorrect_answer, this.data.correct_answer]}></Answers>
				<Submit></Submit>

			</div>
		);
	}

}

	export default App
