import React from "react";
import "./App.css";
import Question from "./components/Question";
import Answer from "./components/Answer";

type Question = {
	categor
}
type S = {
loading: boolean,
questions: string
}

class App extends React.Component {
  private title: string = "Welcome to the Quiz!";

  private data = {
    question: "What is the fastest  land animal?",
    correct_answer: "Cheetah",
    incorrect_answer: ["Lion", "Thomson&rsquo;s Gazelle", "Pronghorn Antelope"],
  };

  getQuestions() {
    fetch(
      "https://opentdb.com/api.php?amount=10&category=27&difficulty=easy&type=multiple"
    )
      .then((res) => res.json())
      .then((json) => {
        this.setState({ loading: false, questions: json.results });
      });
  }

  render() {
    return (
      <div className="App">
        <div id="title">{this.title}</div>
        <Question content={this.data.question} />
        <Answer
          content={[...this.data.incorrect_answer, this.data.correct_answer]}
        />
        {/* <Submit></Submit> */}
      </div>
    );
  }
}

export default App;
