import React from "react";
import "./App.css";
import Question from "./components/Question";
import Answer from "./components/Answer";

type Question = {
  category: string;
  type: string;
  difficulty: string;
  question: string;
  correct_answer: string;
  incorrect_answer: string[];
};

type S = {
  loading: boolean;
  questions: Question[];
};

class App extends React.Component<{}, S> {
  private title: string = "Welcome to the Quiz";

  state: S = {
    loading: true,
    questions: [],
  };

  constructor(props: {}) {
    super(props);
  }

  getQuestions() {
    fetch(
      "https://opentdb.com/api.php?amount=10&category=27&difficulty=easy&type=multiple"
    )
      .then((res) => res.json())
      .then((json) => {
        this.setState({ loading: false, questions: json.results });
      });
  }

  render() {
    return (
      <div className="App">
        <div id="title">{this.title}</div>
		{!this.state.loading &&

		<></>}

        {/* <Submit></Submit> */}
      </div>
    );
  }
}

export default App;
